import React, { useState } from 'react';
import { registerPlayer, loginPlayer } from './playerService';
import { createTeam, getAllTeams } from './teamService';
import MatchConfirmation from './matchConfirmation';

function App() {
  const [player, setPlayer] = useState(null);

  const handleRegister = async () => {
    const newPlayer = await registerPlayer('John Doe', 'john@example.com');
    setPlayer(newPlayer);
  };

  const handleLogin = async () => {
    const loggedInPlayer = await loginPlayer('john@example.com');
    setPlayer(loggedInPlayer);
  };

  return (
    <div>
      <h1>Falta Uno</h1>
      <button onClick={handleRegister}>Register Player</button>
      <button onClick={handleLogin}>Login Player</button>
      {player && <p>Welcome, {player.name}</p>}

      <MatchConfirmation matchId={1} playerId={player?.id} />
    </div>
  );
}

export default App;
