import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import LoginPage from './LoginPage';  // Ensure these files exist and are imported correctly
import RegisterPage from './RegisterPage';
import './App.css';

function App() {
  return (
    <Router>
      <div className="container">
        <Routes>
          {/* Home Page */}
          <Route
            path="/"
            element={
              <div>
                <h1>Falta Uno!</h1>
                <Link to="/login">
                  <button>Login</button>
                </Link>
                <Link to="/register">
                  <button>Register</button>
                </Link>
              </div>
            }
          />

          {/* Login Page */}
          <Route path="/login" element={<LoginPage />} />

          {/* Register Page */}
          <Route path="/register" element={<RegisterPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
