import React, { useState } from 'react';
import { registerPlayer, loginPlayer } from './playerService';
import { createTeam, getAllTeams } from './teamService';
import MatchConfirmation from './matchConfirmation';
import './App.css';

function App() {
  const [email, setEmail] = useState('');  // State to store the user's email
  const [player, setPlayer] = useState(null);
  const [error, setError] = useState(null);

  // const handleRegister = async () => {
  //   try {
  //     const newPlayer = await registerPlayer('John Doe', 'john@example.com');
  //     setPlayer(newPlayer);
  //   } catch (error) {
  //     console.error("Error during registration:", error);
  //   }
  // };

  const handleLogin = async () => {
    try {
      const loggedInPlayer = await loginPlayer(email);  // Send the email to login
      setPlayer(loggedInPlayer);  // Store the logged-in player info
      setError(null);  // Reset error state if successful
    } catch (error) {
      setError("Login failed. Please try again.");  // Set error message if login fails
      console.error("Error during login:", error);
    }
  };

  if (player) {
    return (
      <div>
        <h1>Welcome, {player.name}</h1>
      </div>
    );
  }

  return (
    <div className="container">
      {/* Add the soccer ball above the title */}
      <img src="/soccer-ball.jpg" alt="Soccer Ball" className="soccer-ball" />
      <h1 className="title">Falta Uno!</h1>
      <input 
        type="email" 
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="input"
      />
      <button onClick={handleLogin} className="button">Login</button>
      {error && <p className="error">{error}</p>}
    </div>
  );
}

export default App;
